function onEntry(e){e.forEach((e=>{e.isIntersecting&&(e.target.classList.contains("scroll-item")?e.target.classList.add("animation"):e.target.classList.contains("scroll-dash")&&e.target.classList.add("animation-dash"))}))}let observer=new IntersectionObserver(onEntry,{threshold:[.5]}),elements=document.querySelectorAll(".scroll-item, .scroll-dash");for(let e of elements)observer.observe(e);const slider=document.getElementById("slider"),sliderValueProducts=document.getElementById("sliderValueProducts"),sliderValuePrice=document.getElementById("sliderValuePrice");sliderValueProducts.innerHTML=slider.value+" шт.",sliderValuePrice.innerHTML=5*slider.value+" ₽",slider.oninput=function(){sliderValueProducts.innerHTML=this.value+" шт.",sliderValuePrice.innerHTML=5*this.value+" ₽"};